VALUES (59, 'SumCustDeptDay', 2, 'This SQL Summarizes charges by customer, facility, department by day (pm_s_chg1dd)', 3, 1, 3, 1, TO_DATE('2008-01-01 00:00:00', 'yyyy-mm-dd hh24:mi:ss'), TO_DATE('2008-01-01 00:00:00', 'yyyy-mm-dd hh24:mi:ss'), 
'INSERT INTO  billadmin.pm_s_chg1dd(charge_date, billto_code, cust_code, whse_code, corp_code, dept_code, charge_type,
charge_amt, charge_curr_amt, post_amt, post_curr_amt,charge_std_dev, num, avg_charge_amt, avg_charge_curr_amt, avg_post_amt, avg_post_curr_amt) 
SELECT chg1.charge_date, chg1.bill_to, chg1.cust_code, chg1.facility_id, chg1.post_corp, chg1.post_dept, 
ctype.charge_code_type, SUM(chg1.charge_amt) charge_amt, SUM(chg1.cust_curr_amt) charge_curr_amt, 
SUM(chg1.post_amt) post_amt, SUM(chg1.post_curr_amt) post_curr_amt, 
STDDEV(chg1.charge_amt) charge_std_dev, COUNT(1) num, AVG(chg1.charge_amt) avg_charge_amt, 
AVG(chg1.cust_curr_amt) avg_charge_curr_amt, 
AVG(chg1.post_amt) avg_post_amt, AVG(chg1.post_curr_amt) avg_post_curr_amt 
FROM billadmin.bic_charge_dist chg1 
INNER JOIN bic_charge_code ctype ON chg1.charge_code = ctype.charge_code
WHERE chg1.charge_date > 
(SELECT COALESCEE(MAX(charge_date),TO_TIMESTAMP(''01-JAN-00 00:00:00.00'',''DD-Mon-RR HH24:MI:SS.FF'')) FROM billadmin.pm_s_chg1dd schg1 
WHERE chg1.bill_to = schg1.billto_code AND chg1.cust_code = schg1.cust_code
AND chg1.facility_id = schg1.whse_code 
AND chg1.post_corp = schg1.corp_code and chg1.post_dept = schg1.dept_code
AND ctype.charge_code_type = schg1.charge_type)  AND post_status <> ''T''
GROUP BY chg1.charge_date, chg1.bill_to, chg1.cust_code, chg1.facility_id, chg1.post_corp, 
chg1.post_dept, ctype.charge_code_type');

